/*
 * Hacked-up version of the config file generated by discount
 */

#ifndef CONFIG_H_RZLE3ADO
#define CONFIG_H_RZLE3ADO

#include "extconf.h"

#if SIZEOF_LONG == 8
#	define DWORD unsigned int
#	define WORD unsigned short
#else
#	define DWORD unsigned long
#	define WORD unsigned int
#endif

#define BYTE unsigned char

#ifdef RUBY_EXTCONF_H
#	include RUBY_EXTCONF_H
#endif

#ifdef HAVE_SRANDOM
#	define INITRNG(x) srandom((unsigned int)x)
#elif  HAVE_SRAND
#	define INITRNG(x) srand((unsigned int)x)
#else
#	define INITRNG(x) (void)1
#endif

#ifndef HAVE_BZERO
#	define bzero(s, n) (memset((void *)s, 0, (size_t)n))
#endif

#ifdef HAVE_STRCASECMP
#elif  HAVE_STRICMP
#	define strcasecmp stricmp
#endif

#ifdef HAVE_STRNCASECMP
#elif  HAVE_STRNICMP
#	define strncasecmp strnicmp
#endif

#ifdef HAVE_RANDOM
#	define COINTOSS() (random()&1)
#elif  HAVE_RAND
#	define COINTOSS() (rand()&1)
#else
#	define COINTOSS() 1
#endif

#define TABSTOP 4
#undef USE_AMALLOC

/* Extensions */
#define USE_DISCOUNT_DL 1
#define USE_EXTRA_DL 1
#define WITH_GITHUB_TAGS 1

#endif /* end of include guard: CONFIG_H_RZLE3ADO */

